import React from 'react';
import './Homepage.css';


import phonecases from '../components/images/phonecases.png';
import laptop from '../components/images/laptop.jpg';
import extension from '../components/images/extension.jpg';
import watch from '../components/images/watch.jpg';
import stationery from '../components/images/stationery.jpg';
import powerbank from '../components/images/powerbank.jpg';

import earbuds from '../components/images/earbuds.jpg';
import earpods from '../components/images/earpods.jpg';

import bluetooth from '../components/images/bluetooth.jpg';
import earphones from '../components/images/earphones.jpg';
import bulb from '../components/images/bulb.jpg';
import charger from '../components/images/charger.jpg';
import extensions from '../components/images/extensions.jpg';
import headphones from '../components/images/headphones.jpg';
import keyboard from '../components/images/keyboard.jpg';
import charger2 from '../components/images/charger2.jpg';
import stationary1 from '../components/images/stationary1.jpg';
import keyboard2 from '../components/images/keyboard2.jpg';
import cables from '../components/images/cables.jpg';
import screenprotector from '../components/images/screenprotector.jpg';
import hdmicable from '../components/images/hdmicable.png';
import mousepads from '../components/images/mousepads.jpg';
import usbflash from '../components/images/usbflash.jpg';
import usbhubs from '../components/images/usbhubs.jpg';



function Shop() {

  const handlePurchaseClick = (productName, productImage) => {
    const productImageURL = window.location.origin + productImage; // Generates the full URL of the image
    const message = `Is this available?\nProduct: ${productName}\nImage: ${productImageURL}`;
    const whatsappURL = `https://wa.me/1234567890?text=${encodeURIComponent(message)}`;
    window.open(whatsappURL, '_blank');
  };

  const products = [
    { name: 'Keyboards', image: keyboard2, price: 'KES 200 - 600' },
    { name: 'Phone cases', image: phonecases, price: 'KES 100-500' },
    { name: 'Bluetooth', image: bluetooth, price: 'KES 400-1000' },


    { name: 'Laptops', image: laptop, price: 'KES 200 - 600' },
    { name: 'Extension', image: extension, price: 'KES 100-500' },
{ name: 'Stationeries', image: stationery, price: 'KES 400-1000' },
    { name: 'Watch', image: watch, price: 'KES 200 - 600' },
    { name: 'Power Bank', image: powerbank, price: 'KES 100-500' },

    { name: 'Oraimo Earpods', image: earbuds, price: 'KES 400-1000' },
    { name: 'Oraimo Earbuds', image: earpods, price: 'KES 200 - 600' },
    { name: 'Phone cases', image: phonecases, price: 'KES 100-500' },
    { name: 'Bluetooth Earphones', image: earphones, price: 'KES 400-1000' },
    { name: 'Chargers', image: charger2, price: 'KES 200 - 600' },
    { name: 'Wireless keyboards', image: keyboard, price: 'KES 100-500' },
    { name: 'Quality Bulbs', image: bulb, price: 'KES 400-1000' },
    { name: 'BT Headphones', image: headphones, price: 'KES 200 - 600' },
    { name: 'Extensions', image: extensions, price: 'KES 100-500' },
    { name: 'Complete Charger', image: charger, price: 'KES 400-1000' },

    { name: 'All Stationery', image: stationary1, price: 'KES 400-1000' },
    { name: 'BT Headphones', image: headphones, price: 'KES 200 - 600' },
    { name: 'Extensions', image: extensions, price: 'KES 100-500' },
    { name: 'Complete Charger', image: charger, price: 'KES 400-1000' },
    { name: 'Screen Protector', image: screenprotector, price: 'KES 400-1000' },
    { name: 'HDMI cables', image: hdmicable, price: 'KES 200 - 600' },
    { name: 'Mouse pads', image: mousepads, price: 'KES 100-500' },
    { name: 'USB Flash', image: usbflash, price: 'KES 400-1000' },
    { name: 'USB Hubs', image: usbhubs, price: 'KES 400-1000' },
    { name: 'Cables', image: cables, price: 'KES 400-1000' },
  ]


 

  return (
    <section id="shop">
      <h2>Shop</h2>
      <div className="product-list">
        {products.map((product, index) => (
          <div key={index} className="product-card">
            <img src={product.image} alt={product.name} />
            <h4>{product.name}</h4>
            <p>Various options available</p>
            <p>{product.price}</p>
            <button onClick={() => handlePurchaseClick(product.name, product.image)}>Purchase</button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Shop;
